3)-- PLANO DE CONTAS DA EMPRESA <<PLANO CONTAS>>
SELECT 
BDCODPLAPADRAO
FROM 
TEMPRESAS_REF B 
INNER JOIN (SELECT MAX(BDREFEMP) MXREF FROM TEMPRESAS_REF WHERE BDCODEMP = <<EMPRESA>>) A
ON A.MXREF =  B.BDREFEMP
WHERE
BDCODEMP = <<EMPRESA>>
GROUP BY BDREFEMP, BDCODPLAPADRAO


4)-- MÍNIMO DO BDCODTPLA  <<MIN_CODTPLA>>
SELECT
BDCODTPLA,
MIN(BDCTALON) 
FROM PLANOS_TPLA
WHERE bdcodplapadrao = <<PLANO CONTAS>>
GROUP BY
BDCODTPLA


5)-- MAXIMO DO BDCODTPLA <<MAX_CODTPLA>>
SELECT
BDCODTPLA,
MAX(BDCTALON) 
FROM PLANOS_TPLA
WHERE bdcodplapadrao = <<PLANO CONTAS>>
GROUP BY
BDCODTPLA


